import os
import pkg_resources


class FileMap:
    """
    A mapping class that provides file paths for different model families.

    Attributes:
        models (dict): A dictionary with model family names as keys and their corresponding file paths as values.
            - "llama3": Path to the tokenizer files for the Llama 3 model.
            - "llama3_1": Path to the tokenizer files for the Llama 3.1 model.
    """
    models = {
        "llama3":
        os.path.join(pkg_resources.resource_filename(__name__, ""),
                     "tokenizer_files", "llama3"),
        "llama3_1":
        os.path.join(pkg_resources.resource_filename(__name__, ""),
                     "tokenizer_files", "llama3_1")
    }
